import React, { useState } from "react";
import axios from "axios";

const ThemeViewer = () => {
  const [query, setQuery] = useState("");
  const [summary, setSummary] = useState("");

  const handleTheme = async () => {
    try {
      const res = await axios.get(`http://localhost:8000/theme?q=${query}`);
      setSummary(res.data.themes);
    } catch {
      setSummary("Summary failed.");
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">Summarize Themes</h2>
      <input
        className="border px-2 py-1 w-full mb-2"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Ask to summarize themes..."
      />
      <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleTheme}>
        Summarize
      </button>
      <div className="mt-4 whitespace-pre-wrap">{summary}</div>
    </div>
  );
};

export default ThemeViewer;
