import React, { useState } from "react";
import axios from "axios";

const QueryBox = () => {
  const [query, setQuery] = useState("");
  const [answers, setAnswers] = useState([]);

  const handleQuery = async () => {
    try {
      const res = await axios.get(`http://localhost:8000/query?q=${query}`);
      setAnswers(res.data.answers);
    } catch (err) {
      setAnswers([{ content: "Query failed", meta: {} }]);
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">Ask a Question</h2>
      <input
        className="border px-2 py-1 w-full mb-2"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Type your question..."
      />
      <button className="bg-green-600 text-white px-4 py-2 rounded" onClick={handleQuery}>
        Ask
      </button>
      <ul className="mt-4 list-disc list-inside">
        {answers.map((a, idx) => (
          <li key={idx}>{a.content}</li>
        ))}
      </ul>
    </div>
  );
};

export default QueryBox;
